angular.module('ambaya')
.directive('estornoModal', ['pecasService', 'consultoresService', function(pecasService, consultoresService){
    return{
        restrict: 'E',
        template:'<div id="estorno" class="modal">'+
                    '<div class="modal-content">'+
                        '<h4>Estorno de venda</h4>'+
                        '<ul class="collection with-header">'+
                            '<li class="collection-header"><b>Peças estornadas</b></li>'+
                            '<li class="collection-item" ng-repeat="peca in estornoPecas | orderBy:\'-valor\'">'+
                                   ' <span class="badge">'+
                                        '<a class="clicavel textoVermelho" ng-click="excluirEstorno(peca)">'+
                                                '<i class="material-icons">highlight_off</i>'+
                                        '</a>'+
                                    '</span>'+
                                    '<div class="row"><span class="col s2">{{peca.quantidade}}</span><span class="col s3">{{peca.tipo.label}}</span><span class="col s2">{{peca.valor | currency: \'R$\'}}</span></div>'+
                            '</li>'+
                           ' <li class="collection-item">'+
                                '<form  ng-submit="addPecas($event)" class="addPeca row">'+
                                    '<div class="input-field col m3 s12">'+
                                        '<select style="display:block" ng-model="pecaEstornoTipo" ng-options="item as item.label for item in opcoes" required>'+
                                                '<option value="" disabled selected>Peça</option>'+
                                        '</select>'+
                                    '</div>'+
                                    '<div class="input-field col m3 s12">'+
                                        '<input ng-model="pecaEstornoValor" class="preco" type="number" min=1 max=999 maxlength="10" name="preco" id="pecaPreco" required>'+
                                        '<label for="pecaPrecoreco">Preço</label>' +
                                    '</div>'+
                                    '<div class="input-field col m3 s12">'+
                                        '<input ng-model="pecaEstornoQuantidade" class="quantidade" type="number" min=1 maxlength="10" name="quantidade" id="pecaQuantidade" required>'+
                                        '<label for="pecaPrecoreco">Quantidade</label>' +
                                    '</div>'+
                                   ' <input type="submit" class="btn-floating btn waves-effect waves-light botaoAmarelo right" value="+"></input>'+
                                '</form>'+
                            '</li>'+
                        '</ul>'+
                    '</div>'+
                    '<div class="modal-footer">'+
                        '<input class="waves-effect waves-green btn-flat" type="submit" form="formEstorno" ng-click="estorno()" value="Pronto!" ng-disabled="estornoPecas.length==0"/>'+
                        '<a class="modal-close waves-effect waves-red btn-flat" >Cancelar</a>'+
                    '</div>'+
                '</div>',
        link: function(scope){
            scope.estornoPecas = [];
            scope.opcoes = pecasService.opcoesPecas;
            scope.addPecas = function(e){
                e.preventDefault();
                scope.estornoPecas.push({tipo: scope.pecaEstornoTipo, valor:scope.pecaEstornoValor, quantidade:scope.pecaEstornoQuantidade});
            }
            scope.excluirEstorno = function(peca){
                var idx = scope.estornoPecas.indexOf(peca);
                console.log(idx);
                scope.estornoPecas.splice(idx, 1);
            }
            scope.estorno = function(){
                console.log(scope.estornoPecas);
                var consultorTemp = angular.copy(scope.consultor);
                var cod = "";
                var preco = "";
                var estornoLista = [];
                for(var i=0; i<scope.estornoPecas.length; i++){
                    cod = scope.estornoPecas[i].tipo.val;
                    preco = scope.estornoPecas[i].valor.toString();
                    if(preco.length == 2) preco = "0"+preco;
                    for(var j=0; j<scope.estornoPecas[i].quantidade; j++){
                        estornoLista.push(cod+preco);
                    }
                }
                console.log(scope.consultor);
                var encontrado = false;
                var estornoOk = true;
                for(i=0; i<estornoLista.length; i++){
                    encontrado = false;
                    var precoPeca = pecasService.extraiPreco(estornoLista[i]);
                    var codPeca = pecasService.extraiCod(estornoLista[i]);
                    for(j=0; j<scope.consultor.vendido.length; j++){
                        if(precoPeca=== pecasService.extraiPreco(scope.consultor.vendido[j]) && codPeca === pecasService.extraiCod(scope.consultor.vendido[j])){
                            scope.consultor.estoque.push(scope.consultor.vendido[j])
                            scope.consultor.vendido.splice(j, 1);
                            scope.consultor.totalVendido -= Number(precoPeca);
                            console.log(scope.consultor.vendido);
                            encontrado = true;
                            break;
                        }
                    }
                    if (encontrado == false){
                        Materialize.toast(estornoLista[i]+" não encontrado!", 10000, 'notificacaoRuim');
                        estornoOk = false;
                    }
                }
                if(estornoOk){
                    consultoresService.estorno(scope.consultor, scope.estornoLista, scope.consultor._id).then(
                        function(response){
                            Materialize.toast("Estorno realizado com sucesso!", 5000, 'notificacaoBoa');
                            scope.estornoPecas = [];
                            $('#estorno').modal('close');
                        },
                        function(err){
                            Materialize.toast("Falha ao realizar estorno!", 5000, 'notificacaoRuim');
                            scope.consultor = consultorTemp;
                        }
                    ); 
                } else {
                    Materialize.toast("Falha ao realizar estorno!", 5000, 'notificacaoRuim');
                    scope.consultor = consultorTemp;
                }

                /* */
            }
        }
    }
}]);
angular.module('ambaya')
	.directive('trocaModal', ['pecasService', 'consultoresService', function (pecasService, consultoresService) {
		return {
			restrict: 'E',
			template: '<div id="troca" class="modal">' +
				'<div class="modal-content center-align">' +
				'<form ng-submit="trocaPeca()" id="trocaForm">' +
				'<div class="input-field col m8 s12 offset-m2">' +
				'<input placeholder="Peça com defeito" id="codigoDefeito" type="text" ng-model="troca.pecaDefeito" style="text-transform:uppercase"				 pattern="[A-Za-z]{2}[0-9]{3,6}" title="Código inválido" required>' +
				'<input placeholder="Peça nova" id="codigoNova" type="text" ng-model="troca.pecaNova" style="text-transform:uppercase" pattern="[A-Za-z]{2}[0-9]{3,6}" title="Código inválido" required>' +
				'</div>' +
				'<div class="input-field">' +
				'<select id="defeito" ng-model="troca.defeito" required>' +
				'<option value="" disabled selected>Qual é o defeito?</option>' +
				'<option value="Banho">Banho</option>' +
				'<option value="Bruto">Bruto</option>' +
				'</select>' +
				'</div>' +
				'<div class="input-field col m8 s12 offset-m2">' +
				'<input placeholder="Observações (opcional)" type="text" ng-model="troca.obs">' +
				'</div>' +

				'<div class="row" ng-show="saldo()">' +
				'<div class="col s12 m4 offset-m4">' +
				'<div class="card-panel yellow">' +
				'<span class="black-text">' +
				'Necessário pagamento de mais {{saldo() | currency:"R$"}}' +
				'</span>' +
				'</div>' +
				'</div>' +
				'</div>' +

				'<button type="submit" class="btn-floating btn-large waves-effect waves-light botaoAmarelo">' +
				'<i class="material-icons">import_export</i>' +
				'</button>' +
				'</form>' +
				'</div>' +
				'</div>',
			link: function(scope){
				scope.trocaPeca = function(){
					scope.troca.consultorId = scope.usuario._id;
					scope.troca.consultorNome = scope.usuario.nome;
					var estoqueTemp = [];
					var estoqueTemp = estoqueTemp.concat(scope.usuario.estoque);
					var encontrado = false;
					var cod = scope.troca.pecaNova.toUpperCase();
					var codDefeito = scope.troca.pecaDefeito.toUpperCase();
					var encontrado = false;
					var precoAdd = scope.extraiPreco(cod);
					var codAdd = scope.extraiCod(cod);
					scope.troca.saldo = scope.saldo()
					for (var j=0; j<scope.usuario.estoque.length; j++){
						if(precoAdd=== scope.extraiPreco(scope.usuario.estoque[j]) && codAdd === scope.extraiCod(scope.usuario.estoque[j])){
							scope.deletaElemento(scope.usuario.estoque, j);
							encontrado = true;
							break;
						}
					}
					if (encontrado == false){
						Materialize.toast(cod+" não encontrado!", 10000, 'notificacaoRuim');
						scope.usuario.estoque = estoqueTemp;
					}else{
						console.log(scope.troca);
						consultoresService.atualizaEstoque(scope.usuario).then(
							function(res){
								consultoresService.troca(scope.troca).then(
									function(res){
										if(scope.troca.saldo) scope.usuario.totalVendido = scope.usuario.totalVendido + scope.troca.saldo;
										scope.troca = {};
										$('#troca').modal('close');
										Materialize.toast("Troca registrada com sucesso!", 5000, 'notificacaoBoa');
									},
									function(res){
										Materialize.toast("Falha ao registrar troca!", 5000, 'notificacaoRuim');
									}
								);
							},
							function(res){
								scope.usuario.estoque = estoqueTemp;
								Materialize.toast("Falha ao atualizar estoque!", 5000, 'notificacaoRuim');
							}
						);                   
					}
				}
				scope.saldo = function(){
					if(!scope.troca.pecaNova || !scope.troca.pecaDefeito) return "";
					var saldo = scope.extraiPreco(scope.troca.pecaNova) - scope.extraiPreco(scope.troca.pecaDefeito);
					return saldo>0 ? saldo : 0;
				} 
			}
		}
	}
]);